userDefinedLinkHintCss:
#vimiumHintMarkerContainer div.internalVimiumHintMarker, #vimiumHintMarkerContainer div.vimiumHintMarker {
  padding: 4px 5px;
  background: #263238;
  border: 1px solid #546E7A;
  box-shadow: none;
}

div > .vimiumHintMarker span {
  color: #ABB2BF;
  font-size: inherit;
  text-shadow: none;
}

#vimiumHintMarkerContainer div > .matchingCharacter {
  opacity: 1;
}

#vimiumHintMarkerContainer div > .matchingCharacter ~ span {
  color: #ABB2BF;
}

#vomnibar {
  background: #263238;
  border: none;
  box-shadow: none;
  animation: show 200ms cubic-bezier(0, 0, 0.2, 1) forwards;
}

@keyframes show {
  0% {
    transform: translateY(50px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

#vomnibar .vomnibarSearchArea,
#vomnibar input {
  color: #ABB2BF;
  background: transparent;
  border: none;
}

#vomnibar .vomnibarSearchArea {
  padding: 10px 30px;
}

#vomnibar input {
  padding: 0;
}

#vomnibar ul {
  padding: 0;
  background: #263238;
  border-top: 1px solid #333;
}

#vomnibar li {
  padding: 10px;
  border-bottom: 1px solid #37474F;
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
  padding: 3px 0;
}

#vomnibar li .vomnibarSource {
  color: #546E7A;
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: #ABB2BF;
}

#vomnibar li .vomnibarUrl {
  color: #546E7A;
}

#vomnibar li .vomnibarMatch {
  color: #ABB2BF;
  font-weight: normal;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #ABB2BF;
}

#vomnibar li.vomnibarSelected {
  background-color: #1A2327;
}

div.vimiumHUD {
  background: #1A2327;
  border: none;
  box-shadow: none;
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: #ABB2BF;
}

div.vimiumHUD .hud-find {
  background-color: transparent;
  border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background-color: transparent;
}
